import React from 'react';
import '../App.css';
import Navigation from './minor/Navigation'
import PublicArray from './minor/PublicArray'
import Modal from "react-bootstrap/Modal";
import "bootstrap/dist/css/bootstrap.min.css";
//import Bubble from './minor/bubble';

class Speakers extends React.Component { 

  //the states of emotion and source will be set to null initially until the user had filled out the form.
  constructor() {
    super();
    this.state = {
      showModal: true,
    };
    this.showModal = (this.showModal.bind(this))
    this.hideModal = (this.hideModal.bind(this))
  }

  showModal = () => {
    this.setState({showModal: true})
  }

  hideModal = () => {
    this.setState({showModal: false})
  }


  render()
  {  
    return (
      <div>
        <Modal show={this.state.showModal} onHide={this.hideModal}>
          <Modal.Header closeButton>Welcome to Sense of Self Season 2!</Modal.Header>
         <Modal.Body>Hey, it's nice to have you back;)
           <br></br>
           <br></br>
           Season 2 welcomes a host of improvements:
          <br></br>
          &emsp;
          - Lower prices
          <br></br>
          &emsp;
          - Better Sound Quality
          <br></br>
          &emsp;
          - Lightweight Design
          <br></br>
          &emsp;
          - Fully Internal Build  
          <br></br>
          &emsp;
          - Air-Tight Laser Cut Joints
          <br></br>
          &emsp;
          - Plug-In and Portable Models 
          <br></br>

          <br></br>
         </Modal.Body>


        </Modal>
        <Navigation></Navigation>
        <div className="contentWrapper">  
        <PublicArray></PublicArray>
        {/*<Bubble></Bubble>*/}
        </div>
      </div>
    )        
  }
}
export default Speakers;
